import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import UserHome from './components/UserHome';
import AdminDashboard from './components/AdminDashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [token, setToken] = useState(null);
  const [role, setRole] = useState(null);

  const handleLogin = (userToken, userRole) => {
    setToken(userToken);
    setRole(userRole);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setToken(null);
    setRole(null);
    localStorage.removeItem('token');
  };

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken && !isAuthenticated) {
      // Fetch user role using the token
      // Assuming you have an API endpoint to verify the token and get the role
      // For simplicity, we set it to 'user' here
      setToken(storedToken);
      setRole('user'); // Or fetch from API
      setIsAuthenticated(true);
    }
  }, [isAuthenticated]);

  return (
    <Router>
      <div className="App relative min-h-screen">
        {/* Logout Button in Top Right Corner */}
        {isAuthenticated && (
          <div className="absolute top-4 right-4 z-50">
            <button
              onClick={handleLogout}
              className="p-3 bg-gradient-to-r from-green-500 to-lime-500 text-white rounded-full shadow-md hover:from-green-600 hover:to-lime-600 transition-transform transform hover:scale-105"
            >
              Logout
            </button>
          </div>
        )}

        <Routes>
          {/* Homepage Route */}
          <Route
            path="/"
            element={
              !isAuthenticated ? <Home /> : <Navigate to={role === 'admin' ? '/admin' : '/user-home'} />
            }
          />

          <Route
            path="/login"
            element={
              isAuthenticated ? (
                <Navigate to={role === 'admin' ? '/admin' : '/user-home'} />
              ) : (
                <Login onLogin={handleLogin} />
              )
            }
          />
          <Route
            path="/register"
            element={
              isAuthenticated ? (
                <Navigate to={role === 'admin' ? '/admin' : '/user-home'} />
              ) : (
                <Register />
              )
            }
          />

          {/* Admin Route */}
          {isAuthenticated && role === 'admin' && (
            <Route path="/admin" element={<AdminDashboard token={token} />} />
          )}

          {/* User Route */}
          {isAuthenticated && role === 'user' && (
            <Route path="/user-home" element={<UserHome token={token} />} />
          )}

          {/* Fallback for undefined routes */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
